import 'package:flutter/material.dart';

void main(){
  runApp(myapp());
}
class myapp extends StatelessWidget {
  const myapp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: HomePage(),
    );
  }
}

class HomePage extends StatefulWidget {
  const HomePage({super.key});

  @override
  State<HomePage> createState() => HomePageState();
}

class HomePageState extends State<HomePage> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(appBar: AppBar(title: Center(child: Text("Sliver AppBar")),backgroundColor: Colors.grey),backgroundColor: Colors.amberAccent,
      body: CustomScrollView(
        slivers: <Widget>[
          SliverAppBar(
            pinned: false,
            floating: false,
            expandedHeight: 260.0,
            flexibleSpace: FlexibleSpaceBar(
              title: Text("FLUTTER APP"),
              background: Image.network("data:image/jpeg;base64,",fit: BoxFit.cover,),
            ),
          ),SliverList(delegate: SliverChildBuilderDelegate(
                (BuildContext context,int index) {
              return ListTile(textColor: Colors.pink,
                title: Text('Item ${1+index}'),
              );
            },childCount: 20,
          ),
          ),
        ],
      ),
    );
  }
}


